-- Tạo bảng LOP
CREATE TABLE LOP (
    MALOP NVARCHAR2(4) NOT NULL,
    GVCN NVARCHAR2(25) NULL,
    CONSTRAINT PK_LOP PRIMARY KEY (MALOP)
);

-- Tạo bảng DSHS (Danh sách học sinh)
CREATE TABLE DSHS (
    MAHS NVARCHAR2(5) NOT NULL,
    HO NVARCHAR2(15) NULL,
    TEN NVARCHAR2(7) NULL,
    NU NUMBER(1) NOT NULL, -- Thay thế cho kiểu BIT, 1 = Nữ, 0 = Nam
    NGAYSINH DATE NULL,      -- Thay thế cho kiểu DATETIME
    MALOP NVARCHAR2(4) NULL,
    GHICHU NVARCHAR2(50) NULL,
    XEPLOAI NVARCHAR2(50) NULL,
    CONSTRAINT PK_DSHS PRIMARY KEY (MAHS),
    CONSTRAINT CHK_DSHS_NU CHECK (NU IN (0, 1)) -- Ràng buộc giá trị cho cột NU
);

-- Tạo bảng DIEM
CREATE TABLE DIEM (
    MAHS NVARCHAR2(5) NOT NULL,
    TOAN FLOAT NULL,
    LY FLOAT NULL,
    HOA FLOAT NULL,
    VAN FLOAT NULL,
    DTB FLOAT NULL,
    XEPLOAI NCHAR(10) NULL,
    CONSTRAINT PK_DIEM PRIMARY KEY (MAHS)
);

-- Thêm các ràng buộc khóa ngoại
ALTER TABLE DSHS ADD CONSTRAINT FK_DSHS_LOP FOREIGN KEY (MALOP) REFERENCES LOP(MALOP);
ALTER TABLE DIEM ADD CONSTRAINT FK_DIEM_DSHS FOREIGN KEY (MAHS) REFERENCES DSHS(MAHS);


